import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './chatroom.css';

const ChatRoom = ({ currentUser, selectedRoom, onLogout }) => {
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]); // Stores all messages
    const [unseenMessageId, setUnseenMessageId] = useState(null); // Store the ID of the first unseen message

    const chatAreaRef = useRef(null); // To scroll to the first unseen message

    // Fetch all messages of the current room when the component loads
    useEffect(() => {
        const fetchMessages = async () => {
            try {
                // Fetch all messages for the room
                const response = await axios.get(`http://localhost:8080/message/${selectedRoom.id}`);
                setMessages(response.data); // Update the message list with all messages

                // Fetch unseen messages and highlight them
                const unseenResponse = await axios.get(`http://localhost:8080/message/${selectedRoom.id}/${currentUser.id}/unseenMessages`);
                if (unseenResponse.data.length > 0) {
                    const lastUnseenMessage = unseenResponse.data[unseenResponse.data.length - 1];
                    setUnseenMessageId(lastUnseenMessage.id);

                    // Mark all unseen messages as seen
                    await axios.post(
                        `http://localhost:8080/message/${selectedRoom.id}/${currentUser.id}/lastSeenMessage`,
                        { lastSeenMessageId: lastUnseenMessage.id },
                        { headers: { 'Content-Type': 'application/json' } }
                    );
                }
            } catch (error) {
                console.error('Error fetching messages:', error);
            }
        };

        if (selectedRoom?.id) {
            fetchMessages();
        }
    }, [selectedRoom.id, currentUser.id]);

    // Scroll to the first unseen message when messages or unseenMessageId change
    useEffect(() => {
        if (unseenMessageId && chatAreaRef.current) {
            // Timeout to ensure DOM updates before scrolling
            setTimeout(() => {
                const unseenMessageElement = document.getElementById(`message-${unseenMessageId}`);
                if (unseenMessageElement) {
                    unseenMessageElement.scrollIntoView({ behavior: 'smooth', block: 'center' });
                }
            }, 100); // Delay to ensure DOM updates
        }
    }, [unseenMessageId, messages]);

    const handleLogout = async () => {
        try {
            await axios.put(`http://localhost:8080/join/${currentUser.id}/${selectedRoom.id}`);
            onLogout();
        } catch (error) {
            console.error('Error updating join status:', error);
        }
    };

    const sendMessage = async (e) => {
        e.preventDefault();
        if (message.trim()) {
            try {
                const newMessage = {
                    message: message,
                    user: { id: currentUser.id, username: currentUser.username },
                    room: { id: selectedRoom.id, label: selectedRoom.label }
                };

                const response = await axios.post('http://localhost:8080/message', newMessage);
                const savedMessage = response.data;
                setMessages((prevMessages) => [...prevMessages, savedMessage]); // Add the new message to the list
                setMessage('');

                // Update the last seen message for the current user
                await axios.post(
                    `http://localhost:8080/message/${selectedRoom.id}/${currentUser.id}/lastSeenMessage`,
                    { lastSeenMessageId: savedMessage.id },
                    { headers: { 'Content-Type': 'application/json' } }
                );
            } catch (error) {
                console.error('Error sending message:', error);
            }
        }
    };

    return (
        <div className="chatroom-container">
            <nav className="navbar">
                <span className="navbar-user">Logged in as: {currentUser.username}</span>
                <button className="logout-btn" onClick={handleLogout}>Logout</button>
            </nav>

            <div className="chat-area" ref={chatAreaRef}>
                <h3>Chatting in: {selectedRoom.label}</h3>
                <div className="message-list">
                    {messages.map((msg) => (
                        <div
                            key={msg.id}
                            id={`message-${msg.id}`}
                            className={`message ${unseenMessageId && msg.id >= unseenMessageId ? 'unseen-message' : ''}`}>
                            <strong>{msg.user ? msg.user.username : 'Unknown User'}:</strong> {msg.message || ''}
                        </div>
                    ))}
                </div>

                <form onSubmit={sendMessage} className="message-form">
                    <input
                        type="text"
                        className="message-input"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        placeholder="Type a message..."
                    />
                    <button type="submit" className="send-btn">Send</button>
                </form>
            </div>
        </div>
    );
};

export default ChatRoom;
