import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './chatroom.css'; // You can style the component as needed

const ChatRoom = ({ currentUser, selectedRoom, onLogout }) => {
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]); // Stores sent messages

    // Fetch all messages of the current room when the component loads
    useEffect(() => {
        const fetchMessages = async () => {
            try {
                const response = await axios.get(`http://localhost:8080/message/${selectedRoom.id}`);
                setMessages(response.data); // Set fetched messages to state
                console.log(response.data);
            } catch (error) {
                console.error('Error fetching messages:', error);
            }
        };

        fetchMessages();
    }, [selectedRoom.id]);

    const handleLogout = async () => {
        try {
            // Call the API to update isActive to false
            await axios.put(`http://localhost:8080/join/${currentUser.id}/${selectedRoom.id}`, { isActive: false });

            // Call the onLogout function to handle logout logic in the parent component
            onLogout();
        } catch (error) {
            console.error('Error updating join status:', error);
        }
    };

    const sendMessage = async (e) => {
        e.preventDefault();
        if (message.trim()) {
            try {
                const newMessage = {
                    message: message,  // Ensure this matches your backend's field name (message or content)
                    user: { id: currentUser.id,username: currentUser.username},
                    room: { id: selectedRoom.id,label:selectedRoom.label }
                };
    
                // Save the message in the database and get the saved message from the response
                const response = await axios.post('http://localhost:8080/message', newMessage);
                const savedMessage = response.data;
                console.log(savedMessage);
                // Update the local messages list for immediate display using the exact structure from the backend
                setMessages([...messages, savedMessage]);
    
                // Clear input after sending
                setMessage('');
            } catch (error) {
                console.error('Error sending message:', error);
            }
        }
    };
    
    return (
        <div className="chatroom-container">
            {/* Navbar */}
            <nav className="navbar">
                <span className="navbar-user">Logged in as: {currentUser.username}</span>
                <button className="logout-btn" onClick={handleLogout}>Logout</button>
            </nav>

            {/* Chat Area */}
            <div className="chat-area">
                <h3>Chatting in: {selectedRoom.label}</h3>
                <div className="message-list">
                    {messages.map((msg, index) => (
                        <div key={index} className="message">
                            <strong>{msg.user.username}:</strong> {msg.message}
                        </div>
                    ))}
                </div>

                {/* Input field for new messages */}
                <form onSubmit={sendMessage} className="message-form">
                    <input
                        type="text"
                        className="message-input"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        placeholder="Type a message..."
                    />
                    <button type="submit" className="send-btn">Send</button>
                </form>
            </div>
        </div>
    );
};

export default ChatRoom;
