import React from 'react';
import { BrowserRouter as Router, Route, Redirect, Switch } from 'react-router-dom';
import Login from './component/Login';
import Signup from './component/Signup';
import ChatRoomSelector from './component/Room';
import ChatRoom from './component/ChatRoom';

const App = () => {
    const isAuthenticated = !!localStorage.getItem('token');
    const currentUser = JSON.parse(localStorage.getItem('user')); // Assuming user info is stored in local storage after login

    return (
        <Router>
            <Switch>
                <Route path="/login" component={Login} />
                <Route path="/signup" component={Signup} />
                <Route path="/chatrooms">
                    {isAuthenticated ? <ChatRoomSelector user={currentUser} /> : <Redirect to="/login" />}
                </Route>
                <Route path="/chatroom/:roomId">
                    {isAuthenticated ? (
                        <ChatRoom currentUser={currentUser} />
                    ) : (
                        <Redirect to="/login" />
                    )}
                </Route>
                <Redirect from="/" to={isAuthenticated ? "/chatrooms" : "/login"} />
            </Switch>
        </Router>
    );
};

export default App;
