import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './room.css';
import ChatRoom from './ChatRoom';
import './chatroom.css';

const ChatRoomSelector = ({ currentUser }) => {
    const [chatRooms, setChatRooms] = useState([]);
    const [selectedRoom, setSelectedRoom] = useState(null);

    // Fetch chat rooms from the API
    useEffect(() => {
        const fetchChatRooms = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/v1/rooms'); // Adjust API endpoint
                setChatRooms(response.data);
            } catch (error) {
                console.error('Error fetching chat rooms:', error);
            }
        };
        fetchChatRooms();
    }, []);

    const markUserAsInactive = async (roomId) => {
        try {
            await axios.put(`http://localhost:8080/join/${currentUser.id}/${selectedRoom.id}`);
        } catch (error) {
            console.error('Error marking user as inactive:', error);
        }
    };



    const handleCreateRoom = async () => {
        try {
            const createRoomResponse = await axios.post('http://localhost:8080/api/v1/rooms/create', {
                label: newRoomLabel,
                userId: currentUser.id,
                isPrivate: newRoomPrivacy === 'private',
            });
            if (createRoomResponse.status === 201) {
                setChatRooms([...chatRooms, createRoomResponse.data]); // Add the new room to the list
                setShowCreateRoomForm(false); // Close the form after successful creation
                setNewRoomLabel(''); // Reset the room label
                setNewRoomPrivacy('public'); // Reset the privacy option
            }
        } catch (error) {
            console.error('Error creating room:', error);
        }
    };

    const handleJoinRoom = async (room) => {
        // Mark the user as inactive in the currently selected room (if any)
        if (selectedRoom) {
            await markUserAsInactive(selectedRoom.id);
        }

        try {
            const joinResponse = await axios.post('http://localhost:8080/join', {
                userId: currentUser.id,
                roomId: room.id,
            });

            if (joinResponse.status === 200) {
                setSelectedRoom(room); // Set the selected room after successful join
            } else {
                console.error('Failed to join the room');
            }
        } catch (error) {
            console.error('Error joining room:', error);
        }
    };

    const handleleave = async () => {
        // Mark the user as inactive in the current room
        if (selectedRoom) {
            await markUserAsInactive(selectedRoom.id);
        }

        setSelectedRoom(null); // Reset the selected room
    };

    const handleLogout = async () => {
        // Mark the user as inactive in the current room
        if (selectedRoom) {
            await markUserAsInactive(selectedRoom.id);
        }

        setSelectedRoom(null); // Reset the selected room to show the room selector again
        localStorage.removeItem('currentUser'); // Clear the current user from localStorage
        window.location.reload(); // Refresh the page to reset the state
    };

    return (
        <div className="chat-container">
            <div className="chat-room-selector">
                <div className="user-info">
                    <h2>{currentUser.username}</h2>
                    <button className="btn btn-danger" onClick={handleLogout}>Logout</button>
                </div>
                <h2>Rooms</h2>
                <div className="chat-room-list">
                    {chatRooms.map((room) => (
                        <div key={room.id} className="chat-room">
                            <span>{room.label}</span>
                            <button onClick={() => handleJoinRoom(room)}>Join</button>
                        </div>
                    ))}
                </div>
            </div>
            <div className="chat-room-display">
                {selectedRoom ? (
                    <ChatRoom currentUser={currentUser} selectedRoom={selectedRoom} onLogout={handleLogout} onleave={handleleave} />
                ) : (
                    <div className="chat-area">
                        <h3>Select a room to start chatting.</h3>
                    </div>
                )}
            </div>
        </div>
    );
};

export default ChatRoomSelector;
