import React, { useState } from 'react';
import axios from 'axios';
import './login.css';
import { useNavigate } from 'react-router-dom';
import ChatRoomSelector from './Room';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [currentuser, setCurrentUser] = useState('');
    const [token, setToken] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            // Send the login request to the authentication endpoint
            const response = await axios.post('http://localhost:8080/authenticate', {
                username,
                password
            });

            // If successful, store the token
            const jwtToken = response.data.jwtToken;
            setToken(jwtToken);


            setCurrentUser(response.data.username);

            // Optionally, save the JWT to localStorage for future requests
            localStorage.setItem('token', jwtToken);

            console.log('Login successful:', decodedUser);

        } catch (err) {
            setError('Invalid username or password');
        }
    };

    // If the user is not logged in, show the login form
    if (!currentuser) {
        return (
            <div className="login-container">
                <h1 className="h3 mb-3 fw-normal text-light">Please sign in</h1>
                <form onSubmit={handleLogin}>
                    <div className="form-floating mb-3">
                        <label htmlFor="username" className='te'>Username</label>
                        <input
                            type="text"
                            className="form-control"
                            id="username"
                            placeholder="Username"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                        />
                    </div>
                    <div className="form-floating mb-3">
                        <label htmlFor="password" className='te'>Password</label>
                        <input
                            type="password"
                            className="form-control"
                            id="password"
                            placeholder="Password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                    </div>
                    <button className="btn btn-primary w-100 py-2" type="submit">Sign in</button>
                    {error && <p className="text-danger">{error}</p>}
                </form>
            </div>
        );
    } else {
        // If the user is logged in, redirect to the chat room selector
        return (
            <div>
                <ChatRoomSelector currentUser={currentuser} token={token} />
            </div>
        );
    }
};

export default Login;
