            //         }));
            //     } catch (error) {
            //         console.error('Error fetching viewers for new message:', error);
            //     }
            
            //     scrollToBottom();
            // });
            

            
            
            

            channel.bind('message-seen', (seenData) => {
                console.log("yee");
                const { messageId, viewers } = seenData;
                setViewers(prevViewers => ({
                    ...prevViewers,
                    [messageId]: viewers // Update the viewers list for the message that was seen
                }));
            });

            return () => {
                pusher.unsubscribe(`room-${selectedRoom.id}`);
            };
        }
    }, [selectedRoom?.id]);

    const handleLeaveRoom = async () => {
        try {
            // Update user's active status to leave the room
            await axios.put(`http://localhost:8080/join/${currentUser.id}/${selectedRoom.id}`, { isActive: false });
            onleave(); // Invoke the onLogout function to return to room selector
        } catch (error) {
            console.error('Error leaving room:', error);
        }
    };

    const sendMessage = async (e) => {
        e.preventDefault();
        if (message.trim()) {
            const newMessage = {
                message: message,
                user: { id: currentUser.id, username: currentUser.username },
                room: { id: selectedRoom.id, label: selectedRoom.label }
            };
    
            try {
                await axios.post('http://localhost:8080/message', newMessage);
                console.log('Message sent:', newMessage); // Log the sent message
                setMessage(''); // Clear input after sending
            } catch (error) {
                console.error('Error sending message:', error);
            }
        }
    };
    

    const toggleViewerPopup = (messageId) => {
        if (showViewerPopup === messageId) {
            setShowViewerPopup(null); // Hide viewer list
        } else {
            setShowViewerPopup(messageId); // Show viewer list
        }
    };

    return (
        <div className="chatroom-container">
            <nav className="navbar">
                <span>
                    <img src={profileImage} alt="Avatar" className="avatar" />
                </span>
                <span className="navbar-user"> {currentUser.username}</span>
                <button className="leave-room-btn" onClick={handleLeaveRoom}>Leave Room</button>
            </nav>

            <div className="chat-area" ref={chatAreaRef}>
                <h3>{selectedRoom.label}</h3>
                <div className="message-list">
                    {messages.map((msg) => (
                        <div key={msg.id} id={`message-${msg.id}`} className={`message ${msg.user?.id === currentUser?.id ? 'sent' : 'received'}`}>
                            <img src={profileImage} alt="Avatar" className="avatar" />
                            <div className="message-content">
                                <strong>{msg.user ? msg.user.username : 'Unknown User'}:</strong>
                                <br></br>
                                {msg.message || ''}
                            </div>

                            {msg.user.id === currentUser.id && (
                                <div className="message-viewers" onClick={() => toggleViewerPopup(msg.id)}>
                                    {viewers[msg.id]?.length === 0 && (
                                        <img src={multipleSeenIcon} alt="Not viewed" className="viewer-icon" title="Not viewed" />
                                    )}
                                    {viewers[msg.id]?.length >= 1 && (
                                        <img src={seenIcon} alt="Seen by one" className="viewer-icon" title="Seen by at least one" />
                                    )}
                                </div>
                            )}
                        </div>
                    ))}
                    <div ref={endOfMessagesRef}></div> {/* Marker for the end of messages */}
                </div>

                <form onSubmit={sendMessage} className="message-form">
                    <input
                        type="text"
                        className="message-input"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        placeholder="Type a message..."
                    />
                    <button type="submit" className="send-btn">Send</button>
                </form>
            </div>

            {showViewerPopup && viewers[showViewerPopup] && (
                <ViewerPopup
                    viewers={viewers[showViewerPopup]}
                    onClose={() => setShowViewerPopup(null)}
                />
            )}
        </div>
    );
};

export default ChatRoom;
