import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './login.css';
import ChatRoomSelector from './Room';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [currentUser, setCurrentUser] = useState(null);

    // Check localStorage for an existing logged-in user when the component mounts
    useEffect(() => {
        const storedUser = localStorage.getItem('currentUser');
        if (storedUser) {
            setCurrentUser(JSON.parse(storedUser));
        }
    }, []);

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:8080/api/v1/login', { // Updated to POST
                username,
                password
            });
            const user = response.data;
            // Assuming the backend responds with user details including a token
            if (user) {
                console.log('Login successful:', user);
                setCurrentUser(user);
                localStorage.setItem('currentUser', JSON.stringify(user)); // Store the user in localStorage
                localStorage.setItem('token', user.token); // Store JWT if returned
            } else {
                setError('Invalid username or password');
            }
        } catch (err) {
            console.error(err);
            setError('Invalid username or password'); // Change error message based on your API response
        }
    };

    if (!currentUser) {
        return (
            <div className="login-container">
                <h1 className="h3 mb-3 fw-normal text-light">Please sign in</h1>
                <form onSubmit={handleLogin}>
                    <div className="form-floating mb-3">
                        <label htmlFor="username" className='te'>Username</label>
                        <input
                            type="text"
                            className="form-control"
                            id="username"
                            placeholder="Username"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                        />
                    </div>
                    <div className="form-floating mb-3">
                        <label htmlFor="password" className='te'>Password</label>
                        <input
                            type="password"
                            className="form-control"
                            id="password"
                            placeholder="Password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                    </div>
                    <button className="btn btn-primary w-100 py-2" type="submit">Sign in</button>
                    {error && <p className="text-danger">{error}</p>}
                </form>
            </div>
        );
    } else {
        return (
            <div>
                <ChatRoomSelector currentUser={currentUser} />
            </div>
        );
    }
};

export default Login;
