import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './room.css'; // Import the CSS file
import ChatRoom from './ChatRoom';

const ChatRoomSelector = ({ currentUser }) => {
    const [chatRooms, setChatRooms] = useState([]);
    const [selectedRoom, setSelectedRoom] = useState(null);

    // Fetch chat rooms from the API
    useEffect(() => {
        const fetchChatRooms = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/v1/rooms'); // Adjust API endpoint
                setChatRooms(response.data);
            } catch (error) {
                console.error('Error fetching chat rooms:', error);
            }
        };
        fetchChatRooms();
    }, []);

    const handleJoinRoom = async (room) => {
        try {
            // Create a new Join row in the database
            const joinResponse = await axios.post('http://localhost:8080/join', {
                userId: currentUser.id,   // Send userId from the currentUser object
                roomId: room.id           // Send roomId from the selected room
            });

            if (joinResponse.status === 200) {
                // Successfully joined the room
                console.log(`Successfully joined room: ${room.label}`);
                setSelectedRoom(room); // Set the selected room after successful join
            } else {
                console.error('Failed to join the room');
            }
        } catch (error) {
            console.error('Error joining room:', error);
        }
    };

    const handleLogout = () => {
        setSelectedRoom(null); // Reset the selected room to show the room selector again
    };

    if (selectedRoom) {
        return <ChatRoom currentUser={currentUser} selectedRoom={selectedRoom} onLogout={handleLogout} />;
    } else {
        return (
            <div className="chat-container">
                <div className="chat-room-selector">
                    <h2>Welcome, {currentUser.username}</h2>
                    <h2>Select a Chat Room</h2>
                    <div className="chat-room-list">
                        {chatRooms.map((room) => (
                            <div key={room.id} className="chat-room">
                                <span>{room.label}</span>
                                <button onClick={() => handleJoinRoom(room)}>Join</button>
                            </div>
                        ))}
                    </div>
                </div>
                <div className="chat-area">
                    {selectedRoom ? (
                        <h3>Joined Room</h3>
                    ) : (
                        <p>Select a room to start chatting.</p>
                    )}
                </div>
            </div>
        );
    }
};

export default ChatRoomSelector;
