import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Redirect, Switch } from 'react-router-dom';
import Login from './component/Login';
import Signup from './component/Signup';
import ChatRoomSelector from './component/Room';
import ChatRoom from './component/ChatRoom';

const App = () => {
    const [currentUser, setCurrentUser] = useState(null);
    const [selectedRoom, setSelectedRoom] = useState(null);
    const isAuthenticated = !!localStorage.getItem('token');

    return (
        <Router>
            <Switch>
                <Route path="/login">
                    <Login setCurrentUser={setCurrentUser} />
                </Route>
                <Route path="/signup" component={Signup} />
                <Route path="/chatrooms">
                    {isAuthenticated ? (
                        <ChatRoomSelector user={currentUser} onSelectRoom={setSelectedRoom} />
                    ) : (
                        <Redirect to="/login" />
                    )}
                </Route>
                <Route path="/chatroom/:roomId">
                    {isAuthenticated ? (
                        <ChatRoom
                            currentUser={currentUser}
                            selectedRoom={selectedRoom}
                            onLogout={() => {
                                setCurrentUser(null); // Clear current user on logout
                                localStorage.removeItem('token'); // Optionally clear the token
                            }}
                            onLeave={() => setSelectedRoom(null)} // Reset selected room
                        />
                    ) : (
                        <Redirect to="/login" />
                    )}
                </Route>
                <Redirect from="/" to={isAuthenticated ? "/chatrooms" : "/login"} />
            </Switch>
        </Router>
    );
};

export default App;
