import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import Pusher from 'pusher-js';
import './chatroom.css';
import './ViewerPopup.css'; // Import the CSS for the viewer popup
import ViewerPopup from './popup'; // Import the viewer popup component


import profileImage from './profile.png';
import eyeIcon from './view.png'; // Add a suitable eye icon image for unseen messages
import seenIcon from './double-check.png'; // Add a suitable seen icon image for seen messages
import multipleSeenIcon from './check.png'; // Add a suitable icon for multiple viewers

const ChatRoom = ({ currentUser, selectedRoom, onLogout }) => {
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [unseenMessageIds, setUnseenMessageIds] = useState([]);
    const [viewers, setViewers] = useState({}); // Store viewers for each message
    const [showViewerPopup, setShowViewerPopup] = useState(null); // State to manage visible viewer list
    const chatAreaRef = useRef(null);

    const scrollToUnseenMessage = (unseenMessages) => {
        if (unseenMessages.length > 0) {
            const firstUnseenMessageId = unseenMessages[0].id;
            const unseenMessageElement = document.getElementById(`message-${firstUnseenMessageId}`);
            if (unseenMessageElement) {
                unseenMessageElement.scrollIntoView({ behavior: 'smooth', block: 'center' });
            }
        }
    };

    const fetchMessages = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/message/${selectedRoom.id}`);
            setMessages(response.data);

            const unseenResponse = await axios.get(`http://localhost:8080/message/${selectedRoom.id}/${currentUser.id}/unseenMessages`);
            const unseenMessages = unseenResponse.data;

            if (unseenMessages.length > 0) {
                setUnseenMessageIds(unseenMessages.map(msg => msg.id));
                await axios.post(`http://localhost:8080/message/${selectedRoom.id}/${currentUser.id}/markseen`, unseenMessages.map(msg => msg.id), { headers: { 'Content-Type': 'application/json' } });
                scrollToUnseenMessage(unseenMessages);
            }

            // Fetch viewers for each message
            response.data.forEach(async (msg) => {
                const viewersResponse = await axios.get(`http://localhost:8080/message/${selectedRoom.id}/${msg.id}/viewers`);
                setViewers(prevViewers => ({
                    ...prevViewers,
                    [msg.id]: viewersResponse.data
                }));
            });

        } catch (error) {
            console.error('Error fetching messages:', error);
        }
    };

    const markMessageAsSeen = async (messageId) => {
        try {
            await axios.post(`http://localhost:8080/message/${selectedRoom.id}/${currentUser.id}/markseen`, [messageId], { headers: { 'Content-Type': 'application/json' } });
        } catch (error) {
            console.error('Error marking message as seen:', error);
        }
    };

    useEffect(() => {
        if (selectedRoom?.id) {
            fetchMessages();

            const pusher = new Pusher('26b8c5180fb34a80d337', { cluster: 'eu' });
            const channel = pusher.subscribe(`room-${selectedRoom.id}`);
            channel.bind('new-message', (newMessage) => {
                setMessages((prevMessages) => [...prevMessages, newMessage]);
                markMessageAsSeen(newMessage.id);
            });

            return () => {
                pusher.unsubscribe(`room-${selectedRoom.id}`);
            };
        }
    }, [selectedRoom?.id]);

    const handleLogout = async () => {
        try {
            await axios.put(`http://localhost:8080/join/${currentUser.id}/${selectedRoom.id}`, { isActive: false });
            onLogout();
        } catch (error) {
            console.error('Error updating join status:', error);
        }
    };

    const sendMessage = async (e) => {
        e.preventDefault();
        if (message.trim()) {
            try {
                const newMessage = {
                    message: message,
                    user: { id: currentUser.id, username: currentUser.username, avatar: currentUser.avatar }, // Include avatar
                    room: { id: selectedRoom.id, label: selectedRoom.label }
                };
                const response = await axios.post('http://localhost:8080/message', newMessage);
                const savedMessage = response.data;
                setMessage('');
            } catch (error) {
                console.error('Error sending message:', error);
            }
        }
    };

    const toggleViewerPopup = (messageId, event) => {
        const rect = event.target.getBoundingClientRect(); // Get position of the clicked icon
        const position = {
            top: rect.bottom + window.scrollY, // Bottom of the icon
            left: rect.left + window.scrollX,  // Left of the icon
        };

        if (showViewerPopup?.id === messageId) {
            setShowViewerPopup(null); // Hide viewer list
        } else {
            setShowViewerPopup({ id: messageId, position }); // Show viewer list with position
        }
    };

    return (
        <div className="chatroom-container">
            <nav className="navbar">
                <span className="navbar-user">Logged in as: {currentUser.username}</span>
                <button className="logout-btn" onClick={handleLogout}>Logout</button>
            </nav>

            <div className="chat-area" ref={chatAreaRef}>
                <h3>Chatting in: {selectedRoom.label}</h3>
                <div className="message-list">
                    {messages.map((msg) => (
                        <div
                            key={msg.id}
                            id={`message-${msg.id}`}
                            className={`message ${msg.user.id === currentUser.id ? 'sent' : 'received'}`}>
                            <img src={profileImage} alt="Avatar" className="avatar" />
                            <div className="message-content">
                                <strong>{msg.user ? msg.user.username : 'Unknown User'}:</strong> {msg.message || ''}
                                <span className="message-time">{new Date(msg.createdAt).toLocaleTimeString()}</span>
                                {viewers[msg.id]?.length > 0 && ( // Display seen icon if there are viewers
                                    <span className="seen-icon" onClick={(e) => toggleViewerPopup(msg.id, e)}>üëÅÔ∏è</span>
                                )}
                            </div>
                        </div>
                    ))}
                </div>

                <form onSubmit={sendMessage} className="message-form">
                    <input
                        type="text"
                        className="message-input"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        placeholder="Type a message..."
                    />
                    <button type="submit" className="send-btn">Send</button>
                </form>
            </div>

            {/* Display the viewer popup if it is open */}
            {showViewerPopup && viewers[showViewerPopup] && (
                <ViewerPopup
                    viewers={viewers[showViewerPopup]}
                    onClose={() => setShowViewerPopup(null)}
                />
            )}
        </div>
    );
};

export default ChatRoom;
