// Login.js
import React, { useState } from 'react';
import axios from 'axios';
import '.'
import { useNavigate } from 'react-router-dom';
import ChatRoomSelector from './Room';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [currentuser, setcurrentuser] = useState(false);

    const handleLogin = async (e) => {

        e.preventDefault();
        try {
            const response = await axios.get(`http://localhost:8080/api/v1/users/${username}`);
            const user = response.data;
            if (user && user.password === password) {
                // Successful login (You can store the user data or redirect)
                console.log('Login successful:', user);
                setcurrentuser(true);
            } else {
                setError('Invalid username or password');
            }
        } catch (err) {
            setError('User not found');
        }
    };

    if(!currentuser){
        return (
            <div className="login-container">
                <h1 className="h3 mb-3 fw-normal text-light">Please sign in</h1>
                <form onSubmit={handleLogin}>
                    <div className="form-floating mb-3">
                        <input
                            type="text"
                            className="form-control"
                            id="username"
                            placeholder="Username"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                        />
                        <label htmlFor="username">Username</label>
                    </div>
                    <div className="form-floating mb-3">
                        <input
                            type="password"
                            className="form-control"
                            id="password"
                            placeholder="Password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                        <label htmlFor="password">Password</label>
                    </div>
                    <button className="btn btn-primary w-100 py-2" type="submit">Sign in</button>
                    {error && <p className="text-danger">{error}</p>}
                </form>
            </div>
        );
    }else{

        return(
           <div>
              <ChatRoomSelector/>
           </div>
        );
    }
};

export default Login;
