import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './login.css';
import ChatRoomSelector from './Room';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [currentUser, setCurrentUser] = useState(null);

    // Check localStorage for an existing logged-in user when the component mounts
    useEffect(() => {
        const storedUser = localStorage.getItem('currentUser');
        if (storedUser) {
            setCurrentUser(JSON.parse(storedUser)); // Parse the stored user from localStorage
        }
    }, []);

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            // Sending POST request to login endpoint
            const response = await axios.post('http://localhost:8080/api/v1/users/login', {
                username
            });

            // Assuming the response contains the user data or a JWT token
            if (response.data) {
                const user = response.data; // You might want to check what data you receive
                console.log('Login successful:', user);
                setCurrentUser(user);
                localStorage.setItem('currentUser', JSON.stringify(user)); // Store the user in localStorage

                // Redirect to chat room after login
                window.location.href = '/chat'; // Change this to your desired path
            } else {
                setError('Invalid username or password');
            }
        } catch (err) {
            setError('User not found or login failed');
        }
    };

    // Conditional rendering based on the presence of currentUser
    if (currentUser) {
        // If a user is logged in, render the ChatRoomSelector
        return <ChatRoomSelector currentUser={currentUser} />;
    }

    return (
        <div className="login-container">
            <h1 className="h3 mb-3 fw-normal text-light">Please sign in</h1>
            <form onSubmit={handleLogin}>
                <div className="form-floating mb-3">
                    <label htmlFor="username" className='te'>Username</label>
                    <input
                        type="text"
                        className="form-control"
                        id="username"
                        placeholder="Username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                </div>
                <div className="form-floating mb-3">
                    <label htmlFor="password" className='te'>Password</label>
                    <input
                        type="password"
                        className="form-control"
                        id="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                </div>
                <button className="btn btn-primary w-100 py-2" type="submit">Sign in</button>
                {error && <p className="text-danger">{error}</p>}
            </form>
        </div>
    );
};

export default Login;
