import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './login.css';
import { useNavigate } from 'react-router-dom';
import ChatRoomSelector from './Room';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [currentUser, setCurrentUser] = useState(null);
    const navigate = useNavigate();

    // Check if user data is stored in localStorage when the component mounts
    useEffect(() => {
        const storedUser = localStorage.getItem('currentUser');
        if (storedUser) {
            setCurrentUser(JSON.parse(storedUser));
        }
    }, []);

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.get(`http://localhost:8080/api/v1/users/${username}`);
            const user = response.data;
            if (user && user.password === password) {
                // Successful login: store user data in localStorage
                localStorage.setItem('currentUser', JSON.stringify(user));
                setCurrentUser(user);
                setError('');
            } else {
                setError('Invalid username or password');
            }
        } catch (err) {
            setError('User not found');
        }
    };

    const handleLogout = () => {
        // Clear localStorage and reset state
        localStorage.removeItem('currentUser');
        setCurrentUser(null);
    };

    if (!currentUser) {
        return (
            <div className="login-container">
                <h1 className="h3 mb-3 fw-normal text-light">Please sign in</h1>
                <form onSubmit={handleLogin}>
                    <div className="form-floating mb-3">
                        <label htmlFor="username" className='te'>Username</label>
                        <input
                            type="text"
                            className="form-control"
                            id="username"
                            placeholder="Username"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                        />
                    </div>
                    <div className="form-floating mb-3">
                        <label htmlFor="password" className='te'>Password</label>
                        <input
                            type="password"
                            className="form-control"
                            id="password"
                            placeholder="Password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                    </div>
                    <button className="btn btn-primary w-100 py-2" type="submit">Sign in</button>
                    {error && <p className="text-danger">{error}</p>}
                </form>
            </div>
        );
    } else {
        return (
            <div>
                <ChatRoomSelector currentUser={currentUser} onLogout={handleLogout} />
            </div>
        );
    }
};

export default Login;
