import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import Pusher from 'pusher-js';
import './chatroom.css';
import './ViewerPopup.css'; // Import the CSS for the viewer popup
import ViewerPopup from './popup'; // Import the viewer popup component

import profileImage from './profile.png';
import seenIcon from './double-check.png'; // Add a suitable seen icon image for seen messages
import multipleSeenIcon from './icons8-select-24.png'; // Add a suitable icon for multiple viewers

const ChatRoom = ({ currentUser, selectedRoom, onLogout, onleave }) => {
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [unseenMessageIds, setUnseenMessageIds] = useState([]);
    const [viewers, setViewers] = useState({}); // Store viewers for each message
    const [showViewerPopup, setShowViewerPopup] = useState(null); // State to manage visible viewer list
    const chatAreaRef = useRef(null);
    const endOfMessagesRef = useRef(null); // A ref to track the bottom of the messages

    const scrollToBottom = () => {
        if (endOfMessagesRef.current) {
            endOfMessagesRef.current.scrollIntoView({ behavior: 'smooth' });
        }
    };

    const scrollToUnseenMessage = (unseenMessages) => {
        if (unseenMessages.length > 0) {
            const firstUnseenMessageId = unseenMessages[0].id;
            const unseenMessageElement = document.getElementById(`message-${firstUnseenMessageId}`);
            if (unseenMessageElement) {
                unseenMessageElement.scrollIntoView({ behavior: 'smooth', block: 'center' });
            }
        }
    };

    const fetchMessages = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/message/${selectedRoom.id}`);
            setMessages(response.data);

            const unseenResponse = await axios.get(`http://localhost:8080/message/${selectedRoom.id}/${currentUser.id}/unseenMessages`);
            const unseenMessages = unseenResponse.data;

            if (unseenMessages.length > 0) {
                setUnseenMessageIds(unseenMessages.map(msg => msg.id));
                await axios.post(`http://localhost:8080/message/${selectedRoom.id}/${currentUser.id}/markseen`, unseenMessages.map(msg => msg.id), { headers: { 'Content-Type': 'application/json' } });
                scrollToUnseenMessage(unseenMessages);
            }

            // Fetch viewers for each message
            response.data.forEach(async (msg) => {
                const viewersResponse = await axios.get(`http://localhost:8080/message/${selectedRoom.id}/${msg.id}/${currentUser.id}/viewers`);
                setViewers(prevViewers => ({
                    ...prevViewers,
                    [msg.id]: viewersResponse.data
                }));
            });

            // Scroll to bottom after initial fetch
            scrollToBottom();
        } catch (error) {
            console.error('Error fetching messages:', error);
        }
    };

    const markMessageAsSeen = async (messageId) => {
        try {
            await axios.post(`http://localhost:8080/message/${selectedRoom.id}/${currentUser.id}/markseen`, [messageId], { headers: { 'Content-Type': 'application/json' } });
        } catch (error) {
            console.error('Error marking message as seen:', error);
        }
    };

    useEffect(() => {
        if (selectedRoom?.id) {
            fetchMessages();
    
            const pusher = new Pusher('26b8c5180fb34a80d337', { cluster: 'eu' });
            const channel = pusher.subscribe(`room-${selectedRoom.id}`);
    
            // Bind to the 'new-message' event
            channel.bind('new-message', async (newMessage) => {
                console.log('New message received:', newMessage); // Add logging to debug
    
                // Add the new message to the state
                setMessages((prevMessages) => [...prevMessages, newMessage]);
    
                // Mark the message as seen immediately after receiving it
                markMessageAsSeen(newMessage.id);
    
                // Fetch and update viewers for the new message
                try {
                    const viewersResponse = await axios.get(
                        `http://localhost:8080/message/${selectedRoom.id}/${newMessage.id}/${currentUser.id}/viewers`
                    );
                    setViewers((prevViewers) => ({
                        ...prevViewers,
                        [newMessage.id]: viewersResponse.data, // Update viewers for the new message
                    }));
                } catch (error) {
                    console.error('Error fetching viewers for new message:', error);
                }
    
                // Scroll to bottom when a new message is received
                scrollToBottom();
            });
    
            // Listen for the 'message-seen' event to update viewers
            channel.bind('message-seen', (seenData) => {
                const { messageId, viewers } = seenData;
                setViewers((prevViewers) => ({
                    ...prevViewers,
                    [messageId]: viewers, // Update the viewers list for the message that was seen
                }));
            });
    
            // Cleanup on component unmount or room change
            return () => {
                pusher.unsubscribe(`room-${selectedRoom.id}`);
            };
        }
    }, [selectedRoom?.id]);
    

    const handleLeaveRoom = async () => {
        try {
            // Update user's active status to leave the room
            await axios.put(`http://localhost:8080/join/${currentUser.id}/${selectedRoom.id}`, { isActive: false });
            onleave(); // Invoke the onLogout function to return to room selector
        } catch (error) {
            console.error('Error leaving room:', error);
        }
    };

    const sendMessage = async (e) => {
        e.preventDefault();
        if (message.trim()) {
            try {
                const newMessage = {
                    message: message,
                    user: { id: currentUser.id, username: currentUser.username }, // Include avatar
                    room: { id: selectedRoom.id, label: selectedRoom.label }
                };
                const response = await axios.post('http://localhost:8080/message', newMessage);
                setMessage('');
            } catch (error) {
                console.error('Error sending message:', error);
            }
        }
    };

    const toggleViewerPopup = (messageId) => {
        if (showViewerPopup === messageId) {
            setShowViewerPopup(null); // Hide viewer list
        } else {
            setShowViewerPopup(messageId); // Show viewer list
        }
    };

    return (
        <div className="chatroom-container">
            <nav className="navbar">
                <span>
                    <img src={profileImage} alt="Avatar" className="avatar" />
                </span>
                <span className="navbar-user"> {currentUser.username}</span>
                <button className="leave-room-btn" onClick={handleLeaveRoom}>Leave Room</button>
            </nav>

            <div className="chat-area" ref={chatAreaRef}>
                <h3>{selectedRoom.label}</h3>
                <div className="message-list">
              
                {messages.map((msg) => (
        <div
            key={msg.id}
            id={`message-${msg.id}`}
            className={`message ${msg.user?.id === currentUser?.id ? 'sent' : 'received'}`}>
            
            {/* Avatar */}
            <img src={profileImage} alt="Avatar" className="avatar" />
            
            {/* Message Content */}
            <div className="message-content">
                <strong>{msg.user?.username || 'Unknown User'}:</strong>
                <br />
                {msg.message || ''} 
                <br />
                {/* Format sentAt date */}
                {msg.sentAt ? new Date(msg.sentAt).toLocaleString() : ''}
            </div>

            {/* Viewers section, only for the sender */}
            {msg.user?.id === currentUser?.id && (
                <div className="message-viewers" onClick={() => toggleViewerPopup(msg.id)}>
                    {/* No viewers */}
                    {viewers[msg.id]?.length === 0 && (
                        <img 
                            src={multipleSeenIcon} 
                            alt="Not viewed" 
                            className="viewer-icon" 
                            title="Not viewed" 
                        />
                    )}
                    {/* Seen by one user */}
                    {viewers[msg.id]?.length === 1 && (
                        <img 
                            src={seenIcon} 
                            alt="Seen by one" 
                            className="viewer-icon" 
                            title="Seen by one" 
                        />
                    )}
                    {/* Seen by multiple users */}
                    {viewers[msg.id]?.length > 1 && (
                        <img 
                            src={seenIcon} 
                            alt="Seen by multiple" 
                            className="viewer-icon" 
                            title={`Seen by ${viewers[msg.id].length} users`} 
                        />
                    )}
                </div>
            )}
        </div>
    ))}

                    <div ref={endOfMessagesRef}></div> {/* Marker for the end of messages */}
                </div>

                <form onSubmit={sendMessage} className="message-form">
                    <input
                        type="text"
                        className="message-input"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        placeholder="Type a message..."
                    />
                    <button type="submit" className="send-btn">Send</button>
                </form>
            </div>

            {showViewerPopup && viewers[showViewerPopup] && (
                <ViewerPopup
                    viewers={viewers[showViewerPopup]}
                    onClose={() => setShowViewerPopup(null)}
                />
            )}
        </div>
    );
};

export default ChatRoom;
